import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);				int x =sc.nextInt();		int y=sc.nextInt();		int z=x+y;				if(removezeroes(x)+removezeroes(y)==removezeroes(z)) {			System.out.println("YES");		}else {			System.out.println("NO");		}					}		public static int removezeroes(int x) {		String lol=Integer.toString(x);		String out="";		for(int i=0;i<lol.length();i++) {			if (lol.charAt(i)!='0') {				out+=lol.charAt(i);			}		}		return Integer.parseInt(out);	}}
