import java.util.Scanner;public class Main{	public static void main(String[] args)	{		Scanner scan = new Scanner(System.in);		int n = scan.nextInt();		int[] a = new int[n];		int sum = 0;		for (int i=0;i<n;i++){			a[i] = scan.nextInt();			sum+=a[i];		}		if (sum!=0){			System.out.println("YES\n"+1+"\n"+1+" "+n);		}		else if(n==1)			System.out.println("NO");		else {			int summ=0;			int j;			for(j=0;j<n;j++){				summ+=a[j];				if(summ!=0)break;			}			if (j==n)System.out.println("NO");			else			System.out.println("YES\n"+2+"\n"+1+" "+(j+1)+"\n"+(j+2)+" "+n);		}	}}	 			  	    					   	  					 		
