//package codeforces; import java.util.Scanner;//binary search public class B760 { 	public static void main(String[] args) {		Scanner sc= new Scanner(System.in);		long n=sc.nextLong();		long m=sc.nextLong();		long k=sc.nextLong();		sc.close();		long l=1;		long r=m;		long lp=0;		long rp=0;		long ly=k-1;		long ry=n-k;		long x=m;		while(l<r) {			x=(l+r+1)/2;			if(ly>x-1) {				lp=(x*(x-1))/2+ly-x+1;			}			else {				lp=((2*x-ly-1)*ly)/2;			}			if(ry>x-1) {				rp=(x*(x-1))/2+ry-x+1;			}			else {				rp=((2*x-ry-1)*ry)/2;			}			if(lp+rp+x>m) {				if(r==x) {					break;				}				r=x;			}			else {				l=x;			}			}		System.out.println(l);		} }
