//package practice;import java.io.BufferedReader;import java.lang.*;import java.io.FileReader;import java.io.IOException;import java.math.BigInteger; import java.io.IOException;import java.io.InputStreamReader;import java.math.BigDecimal;import java.math.BigInteger;import java.math.MathContext;import java.util.*;import java.util.LinkedList;import java.util.stream.IntStream; public class Main{	 static class FastReader  	    { 	        BufferedReader br; 	        StringTokenizer st; 	  	        public FastReader() 	        { 	            br = new BufferedReader(new	                     InputStreamReader(System.in)); 	        } 	  	        String next() 	        { 	            while (st == null || !st.hasMoreElements()) 	            { 	                try	                { 	                    st = new StringTokenizer(br.readLine()); 	                } 	                catch (IOException  e) 	                { 	                    e.printStackTrace(); 	                } 	            } 	            return st.nextToken(); 	        } 	  	        int nextInt() 	        { 	            return Integer.parseInt(next()); 	        } 	  	        long nextLong() 	        { 	            return Long.parseLong(next()); 	        } 	  	        double nextDouble() 	        { 	            return Double.parseDouble(next()); 	        } 	  	        String nextLine() 	        { 	            String str = ""; 	            try	            { 	                str = br.readLine(); 	            } 	            catch (IOException e) 	            { 	                e.printStackTrace(); 	            } 	            return str; 	        } 	    } 	 public static void main(String[] args) throws java.lang.Exception		   { 		FastReader sc=new FastReader();		int n=sc.nextInt();		int m=sc.nextInt();		int res=Math.abs(n-m);		if((res==0 || res==1) && !(n==0 && m==0)) {			System.out.println("YES");		}		else {			System.out.println("NO");		}	}}	   
