import java.io.*;import java.util.*;public class Solution {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);                   long n = sc.nextLong(), k = sc.nextLong();            List<Long> allFactors = getFactors(n);            if(allFactors.size()<k){                System.out.println("-1");            }else{                Collections.sort(allFactors);                System.out.println(allFactors.get((int)k-1));            }                   }       public static List<Long> getFactors(long num) {        List<Long> allFactors = new ArrayList();              for (long l = 1; l <= (long)Math.sqrt(num); l++) {            if (num % l == 0) {                allFactors.add(l);                if ((num / l) != l) {                    allFactors.add(num / l);                }            }        }         return allFactors;    }    }
