// Educational Codeforces Round 17 B. USB vs. PS/2// https://codeforces.com/contest/762/problem/B import java.io.*;import java.util.*; public class usbps2 {	public static void main(String[] args) throws IOException {		BufferedReader f = new BufferedReader(new InputStreamReader(System.in));		PrintWriter out = new PrintWriter(System.out);				StringTokenizer st = new StringTokenizer(f.readLine());		int a = Integer.parseInt(st.nextToken());		// USB ports		int b = Integer.parseInt(st.nextToken());		// PS/2 ports		int c = Integer.parseInt(st.nextToken());		// Number with both		int m = Integer.parseInt(f.readLine());			// Number of mice for sale		Mouse[] mice = new Mouse[m];					// Stores costs of mice				// Reading in value of mice[]		for (int i = 0; i < m; i++) {			st = new StringTokenizer(f.readLine());			int cost = Integer.parseInt(st.nextToken());			if (st.nextToken().charAt(0) == 'U') {				mice[i] = new Mouse(cost, 0);			} else {				mice[i] = new Mouse(cost, 1);			}		}				// We can sort mice[], and then greedy by choosing the cheapest mouse that we can use		Arrays.sort(mice);		int count = 0;									// Number of computers we can equip		long cost = 0;									// Total cost		for (Mouse currM : mice) {			if (currM.type == 0 && a > 0) {				// Current mouse is USB mouse				a--;				count++;				cost += currM.cost;				continue;			} else if (currM.type == 1 && b > 0) {				// Current mouse is PS/2 mouse				b--;				count++;				cost += currM.cost;				continue;			} else if (c > 0) {				// We can only use a both computer				c--;				count++;				cost+= currM.cost;			}			// Nothing works, so we discard the current mouse		}				// Printing answers		out.println(count + " " + cost);				f.close();		out.close();	}		static class Mouse implements Comparable<Mouse> {		int cost, type;		public Mouse(int c, int t) {			cost = c;			type = t;									// 0 is USB, 1 is PS/2		}				public int compareTo(Mouse m2) {			return Integer.compare(cost, m2.cost);		}	}}
