# https://codeforces.com/contest/762/problem/B a, b, c = map(int, input().split())m = int(input()) def merge(arr1, arr2):    result = []     ptr1 = ptr2 = 0    while ptr1 < len(arr1) or ptr2 < len(arr2):        if ptr1 >= len(arr1):            result.append(arr2[ptr2])            ptr2 += 1        elif ptr2 >= len(arr2):            result.append(arr1[ptr1])            ptr1 += 1        elif arr1[ptr1] < arr2[ptr2]:            result.append(arr1[ptr1])            ptr1 += 1        else:            result.append(arr2[ptr2])            ptr2 += 1     return result usb = []ps2 = [] for i in range(m):    cost, type = input().split()    if type == 'USB':        usb.append(int(cost))    else:        ps2.append(int(cost)) usb.sort()ps2.sort() usbSold = min(len(usb), a)usbCost = sum(usb[:usbSold]) ps2Sold = min(len(ps2), b)ps2Cost = sum(ps2[:ps2Sold]) combined = merge(usb[usbSold:], ps2[ps2Sold:])combinedSold = min(len(combined), c)combinedCost = sum(combined[:combinedSold]) print('{} {}'.format(usbSold + ps2Sold + combinedSold, usbCost + ps2Cost + combinedCost))
