// Working program with FastReaderimport java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.HashSet;import java.util.Set;import java.util.StringTokenizer;     public class Main {	static class FastReader {		BufferedReader br;		StringTokenizer st; 		public FastReader()		{			br = new BufferedReader(				new InputStreamReader(System.in));		} 		String next()		{			while (st == null || !st.hasMoreElements()) {				try {					st = new StringTokenizer(br.readLine());				}				catch (IOException e) {				}			}			return st.nextToken();		} 		int nextInt() { return Integer.parseInt(next()); } 		long nextLong() { return Long.parseLong(next()); } 		double nextDouble()		{			return Double.parseDouble(next());		} 		String nextLine()		{			String str = "";			try {				str = br.readLine();			}			catch (IOException e) {			}			return str;		}	}         public static void main(String[] args)        {            FastReader sc = new FastReader();            int n = sc.nextInt();            int x = n;            Set<Integer> set = new HashSet<>();            for(int i = 0; i < n; i++) {                set.add(sc.nextInt());                while(set.contains(x)) {                    System.out.print(x + " ");                    x--;                }                System.out.println();            }    }                  }                                                                                                                                               
