import java.util.Scanner;public class Main {static Scanner r=new Scanner(System.in);    public static void main(String[] args) {        int n;      n= r.nextInt();      int []x=new int[n+1];       for (int i=1;i<=n;i++)       {         x[i]=r.nextInt();       }       boolean []vis=new boolean[n+1];       int c=n;     for (int i=1;i<=n;i++)       {           vis[x[i]]=true;           while (vis[c]&&c>0)               System.out.print(c--+" ");           System.out.print("\n");       }    }}
