import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);				int n=sc.nextInt();		int min=Integer.MAX_VALUE;		int max=Integer.MIN_VALUE;		int arr[]=new int[n];		for(int i=0;i<n;i++) {			int x=sc.nextInt();			min=Math.min(min, x);			max=Math.max(max, x);			arr[i]=x;		}		int count=0;		for(int i=0;i<n;i++) {			if(arr[i]!=min&arr[i]!=max) {				count++;			}		}		System.out.println(count);			}}
