import java.util.*;public class OathOfNightsWatch{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int[] arr = new int[n];		for(int i = 0;i<n;i++)		{			arr[i] = sc.nextInt();		}		Arrays.sort(arr);		int out = 0;		int max = arr[n-1];		int min = arr[0];		for(int i = 1;i<n-1;i++)		{			if(min<arr[i] && max>arr[i])				out++;		}		System.out.println(out);	}}
