# -*- coding: utf-8 -*-# @Time    : 2021/2/4 8:07 下午# @Author  : qu# @Email   : quzhenqing@zju.edu.cn# @File    : B. Code For 1 bs.pyfrom sys import stdin  def find(n, l, r):    if l > R or r < L:        return 0    if n < 2:        if l >= L and r <= R:            return n        else:            return 0    else:        mid = (l + r) // 2        return find(n // 2, l, mid - 1) + find(n % 2, mid, mid) + find(n // 2, mid + 1, r)  N, L, R = map(int, stdin.readline().split())# 找到展开后的长度,即二分的右边界length = 1while length < N:    length = length * 2 + 1print(find(N, 1, length))
