import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);				int n=sc.nextInt();		int m=sc.nextInt();				int l='a';		int unique=0;				String out="";		boolean flag=true;				while(out.length()<n) {			if(unique<m) {				out+=(char)l;				l++;				unique++;			}else {				if(flag) {					out+='a';				}else {					out+='b';				}				flag=!flag;			}		}		System.out.println(out);			}}
