// Author : Sarthak Kapoor#include<bits/stdc++.h>#include <ext/pb_ds/assoc_container.hpp>using namespace std;using namespace __gnu_pbds;#define rep(i,n) for(int i=0;i<n;++i)#define repa(i,a,n) for(int i=a;i<n;++i)#define repr(i,n) for(int i=n-1;i>=0;--i)#define repba(i,b,a) for(int i=b;i>=a;--i)#define repab(i,a,b) for(int i=a;i<b;++i)#define ll long long#define pi pair<int,int> #define pii pair<pi,int>#define ull unsigned long long#define vi vector<int>#define viip vector<pair<int,pair<int,int> > > #define mp make_pair#define vip vector<pair<int,int> >#define pb push_back #define fi first#define sec second#define all(v) v.begin(),v.end()#define s(v) v.size()ll mod = 1000000007;mt19937 rng(chrono::steady_clock::now().time_since_epoch().count());//use rng()%n for numbers in range [0,n-1]ll gcd(ll a,ll b){	if(b==0)return a;	return gcd(b,a%b);}ll fastexp(ll x,ll a){    ll res = 1;    while(a>0){        if(a&1){            res = (res*x)%mod;        }        a=a>>1;        x=(x*x)%mod;    }    return res;}ll inverse(ll n){    return fastexp(n,mod-2);}template <typename T>void add(T &a, T b){	a += b;	if(a >= mod)a -= mod;}template <typename T>void sub(T &a, T b){	a -= b;	if(a < 0)a += mod;}template <typename T>void mul(T &a, T b){	a *= b;	if(a >= mod)a %= mod;}int main() {	ios::sync_with_stdio(0);	cin.tie(0);	cout.tie(0);	int n;	cin>>n;	int a[n][5];	rep(i,n){		rep(j,5){			cin>>a[i][j];		}	}	vi pos(5,0);	rep(i,5){		if(a[0][i] < a[1][i] && a[0][i] != -1){			pos[i] = 1;		}		else if(a[1][i] == -1){			pos[i] = 1;		}	}	int sz = s(pos);	if(sz == 0){		cout<<"-1";		return 0;	}	vi solved(5,0);	rep(i,5){		int s = 0;		rep(j,n){			if(a[j][i] != -1){				s++;			}		}		solved[i] = s;		// cout<<solved[i]<<"\n";	}	rep(k,100000){		// int tot = 0;		int n2 = n+k;		vi points(5,0);		rep(i,5){			int po = 2;			rep(h,5){				if(po*solved[i] > n2){					points[i] = (h+1)*500;					break;				}				po *= 2;			}			if(points[i] == 0){				points[i] = 3000;			}		}		int x[2] = {0,0};		// int one,two;		// one = two = 0;		rep(i,5){			rep(j,2){				if(a[j][i] != -1){					int temp = 250-a[j][i];					int temp2 = points[i]/250;					int d = temp*temp2;					x[j] += d;					// cout<<i<<" "<<j<<"\n";				}			}		}		// if(k == 3){		// 	cout<<x[0]<<" "<<x[1]<<"\n";		// 	rep(j,5){		// 		cout<<points[j]<<"\n";		// 	}		// }		if(x[0] > x[1]){			cout<<k<<"\n";			return 0;		}		rep(j,5){			if(pos[j]){				;			}			else if(a[0][j] != -1){				solved[j]++;			}		}	}	cout<<"-1";	return 0;}
