//package albert.codeforces.stringgame778a; import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        String s = scan.next();        String t = scan.next();        final int n = s.length();        int[] idx = new int[n];        int[] deleted = new int[n];        for (int i = 0; i < n; i++) {            idx[i] = scan.nextInt() - 1;            deleted[idx[i]] = i;        }        int lo = 0;        int hi = s.length() - t.length();        while (lo <= hi) {            int mid = lo + (hi - lo) / 2;            if (can(mid, s, t, deleted)) {                lo = mid + 1;            } else {                hi = mid - 1;            }        }        System.out.println(hi);    }     private static boolean can(int mid, String s, String t, int[] deletedAt) {        int x = 0;        for (int i = 0; i < t.length(); i++) {            while (x < s.length() && (s.charAt(x) != t.charAt(i) || deletedAt[x] < mid)) {                x++;            }            if (x == s.length()) {                return false;            }            x++;        }        return true;    }  }
