import java.util.*;public class Main{	public static void main(String[] args) {	    Scanner sc = new Scanner(System.in);	    int n = sc.nextInt();	    int arr1[] = new int[n];	    int arr2[] = new int[n];	    int cnt[] = new int[6];	    for(int i=0;i<n;i++){	        arr1[i] = sc.nextInt();	        cnt[arr1[i]]++;	    }	    for(int i=0;i<n;i++){	        arr2[i] = sc.nextInt();	        cnt[arr2[i]]--;	    }	    boolean f = true;	   for(int i=1;i<=5;i++)	   {	       if(cnt[i]<0)	        cnt[i] = -cnt[i];	        if(cnt[i]%2 != 0)	            f =false;	   }	   if(f){	       int ans = 0;	       for(int i=1;i<=5;i++)	           ans += cnt[i];	       System.out.println(ans/4);	   }	   else	       System.out.println("-1");	   	}}
