//package graphs;import java.util.*;public class PW { 	public static void main(String[] args) {		// TODO Auto-generated method stub		Scanner s=new Scanner(System.in);		long n=s.nextLong();		int k=s.nextInt();		int zero=0,count=0;		List<Long> ans=new ArrayList<>();				long tn=n;		while(tn!=0)		{			long rem=tn%10;			ans.add(rem);			tn=tn/10;		}				for(int i=0;i<ans.size();i++)		{			if(ans.get(i)==0)				zero++;			else				count++;						if(zero==k)				break;		}				if(n==0)			System.out.println(0);		else if(k>=ans.size()||zero!=k)			System.out.println(ans.size()-1);		else		System.out.println(count);							}	public static boolean prime(long n)	{		if(n<=2)			return true;				for(int i=2;i<=Math.sqrt(n);i++)		{			if(n%i==0)				return false;		}				return true;			}} 	 			  		 	 		  		 	 		  		
