import java.io.*;import java.util.HashSet;import java.util.StringTokenizer;// نورت الكود يا كبير اتفضل // يا رب Acceptedpublic class AndryushaAndSocks {    public static void main(String[] args) {        FastReader in = new FastReader();        PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         int n = in.nextInt();        HashSet<Integer> set = new HashSet<>();         int counter = 0;        int max = 0;         for (int i = 0; i < n * 2; i++) {            int x = in.nextInt();            counter = (set.contains(x)) ? counter + 1 : Math.max(0, counter - 1);            set.add(x);            max = Math.max(counter, max);        }         out.println(max);         out.close();    }     private static class FastReader {        BufferedReader br;        StringTokenizer st;         FastReader() {            br = new BufferedReader(new                    InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }     }}
