import java.util.*;public class AntonPoly{public static void main(String[] args){Scanner s=new Scanner(System.in);int n=s.nextInt();int out=0;for(int i=0;i<n;i++){String str=s.next();if(str.equals("Tetrahedron")){out=out+4;}else if(str.equals("Cube")){out=out+6;}else if(str.equals("Octahedron")){out=out+8;}else if(str.equals("Dodecahedron")){out=out+12;}else if(str.equals("Icosahedron")){out=out+20;}}System.out.println(out);}}
