import java.util.*;public class Anton{    public static void main(String[] args){        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();              int sum=0;                for(int i=0;i<n;i++)        {            String s=sc.next();            if(s.contains("Tetrahedron")) sum=sum+4;            else if(s.contains("Cube")) sum=sum+6;            else if(s.contains("Octahedron")) sum=sum+8;            else if(s.contains("Dodecahedron")) sum=sum+12;            else if(s.contains("Icosahedron")) sum=sum+20;        }        System.out.println(sum);    }}
