//package practice;import java.io.BufferedReader;import java.lang.*;import java.io.FileReader;import java.io.IOException;import java.math.BigInteger; import java.io.IOException;import java.io.InputStreamReader;import java.math.BigDecimal;import java.math.BigInteger;import java.math.MathContext;import java.util.*;import java.util.LinkedList;import java.util.stream.IntStream; public class Main{	 static class FastReader  	    { 	        BufferedReader br; 	        StringTokenizer st; 	  	        public FastReader() 	        { 	            br = new BufferedReader(new	                     InputStreamReader(System.in)); 	        } 	  	        String next() 	        { 	            while (st == null || !st.hasMoreElements()) 	            { 	                try	                { 	                    st = new StringTokenizer(br.readLine()); 	                } 	                catch (IOException  e) 	                { 	                    e.printStackTrace(); 	                } 	            } 	            return st.nextToken(); 	        } 	  	        int nextInt() 	        { 	            return Integer.parseInt(next()); 	        } 	  	        long nextLong() 	        { 	            return Long.parseLong(next()); 	        } 	  	        double nextDouble() 	        { 	            return Double.parseDouble(next()); 	        } 	  	        String nextLine() 	        { 	            String str = ""; 	            try	            { 	                str = br.readLine(); 	            } 	            catch (IOException e) 	            { 	                e.printStackTrace(); 	            } 	            return str; 	        } 	    } 	 public static void main(String[] args) throws java.lang.Exception		   { 		FastReader sc=new FastReader();		int a=sc.nextInt();		int b=sc.nextInt();		int c=sc.nextInt();		int d=sc.nextInt();			int tc=100000;		int ans=-1;		while(tc-->0) {		   if(b==d) {			   ans=b;			   break;		   }		   if(b<=d) {			   b +=a;  		   }		   else {			   d +=c;		   }		}			System.out.println(ans);	}}	   
