//package cf.c787; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.*; public class C {     public static void main(String[] args) throws IOException {        final BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        final StringTokenizer st = new StringTokenizer(br.readLine());        int n = Integer.parseInt(st.nextToken());        int m = Integer.parseInt(st.nextToken());         for (int i = 0; i < m; i++) {            final StringTokenizer st2 = new StringTokenizer(br.readLine());            int k = Integer.parseInt(st2.nextToken());            Set<Integer> gr = new HashSet<>();            boolean no = false;            for (int j = 0; j < k; j++) {                final int p = Integer.parseInt(st2.nextToken());                if (gr.contains(-p)) {                    no = true;                    break;                }                gr.add(p);            }            if (!no) {                System.out.println("YES");                return;            }        }        System.out.println("NO");    }}
