# 793Bimport collectionsdef do():    row, col = map(int, input().split(" "))    g = [[""]*col for _ in range(row)]    sx = sy = tx = ty = 0    for i in range(row):        for j,c in enumerate(input()):            g[i][j] = c            if c == "S":                sx, sy = i, j            elif c == "T":                tx, ty = i, j    stack = [[sx, sy, -1, 0]]    seen = [[[3,3,3,3] for __ in range(col)] for _ in range(row)]    seen[sx][sy] = [0,0,0,0]    # d = {-1:"", 0:"UP", 1:"DOWN", 2:"left", 3:"right"}    while stack:        # s = stack[-1]        # print([s[0], s[1], d[s[2]], s[3]])        x, y, direction, count = stack.pop()        if count == 3:            continue        if x == tx and y == ty:            return "YES"        if 0<=x-1<row and 0<=y<col and g[x-1][y] != '*':            nc = count if direction == -1 else count + (direction != 0)            if seen[x-1][y][0] > nc:                seen[x-1][y][0] = nc                stack.append([x-1, y, 0, nc])        if 0<=x+1<row and 0<=y<col and g[x+1][y] != '*':            nc = count if direction == -1 else count + (direction != 1)            if seen[x+1][y][1] > nc:                seen[x+1][y][1] = nc                stack.append([x+1, y, 1, nc])        if 0<=x<row and 0<=y-1<col and g[x][y-1] != '*':            nc = count if direction == -1 else count + (direction != 2)            if seen[x][y-1][2] > nc:                seen[x][y-1][2] = nc                stack.append([x, y-1, 2, nc])        if 0<=x<row and 0<=y+1<col and g[x][y+1] != '*':            nc = count if direction == -1 else count + (direction != 3)            if seen[x][y+1][3] > nc:                seen[x][y+1][3] = nc                stack.append([x, y+1, 3, nc])    return "NO"Â print(do())
