import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int m=sc.nextInt();        int k=sc.nextInt();        int[] a=new int[n+1];        for(int i=1;i<n+1;i++)        {            a[i]=sc.nextInt();        }        int min=0,dis=100;        for(int i=1;i<n+1;i++)        {            if(a[i]==0)            {                continue;            }            else             {                if(k>=a[i])                {                    if(m>i)                    {                        min=m-i;                    }                    else                    {                        min=i-m;                    }                }                else                {                    continue;                }            }            dis=Math.min(dis,min);        }        System.out.println(dis*10);    }  }         
