import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);		int n=sc.nextInt();		int k=sc.nextInt();		LinkedList<Integer> list=new LinkedList<>();		for(int i=2;i<=n;i++) {			while(n%i==0) {				n/=i;				list.add(i);			}		}						if (list.size()<k) {			System.out.println(-1);		}else {			int first=1;			while(list.size()>=k) {				first*=list.remove();			}			System.out.print(first+" ");			while(list.size()>1) {				System.out.print(list.remove()+" ");			}			if(list.size()>0)			System.out.println(list.remove());		}	}	}
