import java.io.*;import java.util.*;import java.math.*;import static java.lang.Math.*;import static java.lang.Integer.parseInt;import static java.lang.Long.parseLong;import static java.lang.Double.parseDouble;import static java.lang.String.*; public class Main {        public static void main(String[] args) throws IOException {        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));        StringBuilder out = new StringBuilder();        StringTokenizer tk;                tk = new StringTokenizer(in.readLine());        int n = parseInt(tk.nextToken()),t = parseInt(tk.nextToken()),k = parseInt(tk.nextToken()),d = parseInt(tk.nextToken());        int g = (n + k-1)/k;                int o1 = 0, o2 = d;        for(int i=0; i<g; i++) {            if(o1 <= o2) o1 += t;            else o2 += t;        }                if(max(o1,o2) < g*t) System.out.println("YES");        else System.out.println("NO");    }    }
