import java.util.*;import java.io.*;public final class Codeforces{    public static Scanner sc=new Scanner(System.in);    public static char[][] grid=new char[400][400];    public static int neighbour(int x,int y){        int counter=0;        if(grid[x+1][y]=='.'){            counter++;        }        if(grid[x-1][y]=='.'){            counter++;        }        if(grid[x][y-1]=='.'){            counter++;        }        if(grid[x][y+1]=='.'){            counter++;        }        return counter;    }    public static void main(String[] args){        String s=sc.nextLine();        int n=s.length();        int x=200,y=200;        for(int i=0;i<400;i++){            for(int j=0;j<400;j++){                grid[i][j]='#';            }        }        grid[x][y]='.';        for(int i=0;i<n;i++){            char ch=s.charAt(i);            if(ch=='L'){                x--;            }            else if(ch=='R'){                x++;            }            else if(ch=='U'){                y++;            }            else{                y--;            }            if(grid[x][y]=='.'){                System.out.print("BUG");                return;            }            else{                grid[x][y]='.';            }        }        if(neighbour(200,200)==1 && neighbour(x,y)==1){            x=200;y=200;            for(int i=0;i<n-1;i++){                char ch=s.charAt(i);                if(ch=='L'){                    x--;                }                else if(ch=='R'){                    x++;                }                else if(ch=='U'){                    y++;                }                else{                    y--;                }                if(neighbour(x,y)>2){                    System.out.print("BUG");                    return;                }            }            System.out.print("OK");        }        else{            System.out.print("BUG");        }    }}
