//package cf.c801; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader; public class A {     public static void main(String[] args) throws IOException {        final BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        final String s = br.readLine();         int res = 0;        boolean changed = false;        int prevUsed = -1;        for (int i = 1; i < s.length(); i++) {            char cur = s.charAt(i);            char prev = s.charAt(i - 1);            char next = i == s.length() - 1 ? 'V' : s.charAt(i + 1);             if (prevUsed == i - 1) continue;             if (prev == 'V' && cur == 'K') {                res++;                prevUsed = i;            } else if (!changed && ((prev == 'V' && cur == 'V' && next != 'K') || (prev == 'K' && cur == 'K'))) {                res++;                changed = true;                prevUsed = i;            }        }        System.out.println(res);    }}
