// Working program with FastReaderimport java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import java.util.Collections;import java.util.StringTokenizer;     public class Main {	static class FastReader {		BufferedReader br;		StringTokenizer st; 		public FastReader()		{			br = new BufferedReader(				new InputStreamReader(System.in));		} 		String next()		{			while (st == null || !st.hasMoreElements()) {				try {					st = new StringTokenizer(br.readLine());				}				catch (IOException e) {				}			}			return st.nextToken();		} 		int nextInt() { return Integer.parseInt(next()); } 		long nextLong() { return Long.parseLong(next()); } 		double nextDouble()		{			return Double.parseDouble(next());		} 		String nextLine()		{			String str = "";			try {				str = br.readLine();			}			catch (IOException e) {			}			return str;		}	}         public static void main(String[] args){            FastReader sc= new FastReader();             int n = sc.nextInt();             ArrayList<Integer> a1 = new ArrayList<>();             ArrayList<Integer> a2 = new ArrayList<>();             boolean flag1 = true, flag2 = true;                         for (int i = 0; i < n*2; i++){                if (i%2 == 0){                    a1.add(sc.nextInt());                 }else{                    a2.add(sc.nextInt());                 }            }                        if (!a1.equals(a2)){                flag1 = false;             }                        Collections.sort(a2);            Collections.reverse(a2);            if (!a1.equals(a2)){                flag2 = false;             }                        if (flag1 == true && flag2 == true){                System.out.println("maybe");            }            else if (flag1 == true && flag2 == false){                System.out.println("unrated");            }            else                System.out.println("rated");                                                                                                       }    }            
