import java.io.IOException;import java.util.*;public class Petya2 {    public static void main(String[] args) throws IOException {        Scanner input = new Scanner(System.in);        String x = input.next();        Stack<Character> stk = new Stack<>();        for(int i = x.length()-1; i >= 0; i--)        {            if(!stk.empty() && stk.peek() == x.charAt(i)) {                stk.pop();            }            else                stk.push(x.charAt(i));        }         int size = stk.size();        StringBuffer ans=new StringBuffer("");                for(int i = 0; i < size; i++)            ans.append(stk.pop());                System.out.println(ans);    }}
