# link: https://codeforces.com/contest/810/problem/Bif __name__ == "__main__":    n,s = map(int, input().split())    nn = n    info = []    result = 0    for i in range(nn):         k,l = map(int, input().split())         result = result + min(k,l)        if k>=l:            info.append(0)        else:            info.append( min(2*k, l) - min(k,l) )    info.sort()    info = info[::-1]    i = 0    while i<n and s:        if info[i] > 0:            result += info[i]            s -= 1        i += 1    print(result)        
