import java.util.*; public class Solution {	public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int n = scan.nextInt();		int k = scan.nextInt();		int[] a = new int[n];		int i;		for(i = 0; i < n; i++) {			a[i]= scan.nextInt();		}		List<Integer> b = new ArrayList<>();		for(i = 0; i < k; i++) {			b.add(scan.nextInt());		}		Collections.sort(b);		int aInd = 0;		for(i = k-1; i >= 0; i--) {			while(aInd < n && a[aInd] != 0) aInd++;			a[aInd] = b.get(i);		}		String result = "No";		for(i = 0; i < n-1; i++) {			if(a[i] > a[i+1]) {				result = "Yes";				break;			}		}		System.out.println(result);	}}
