# url: https://codeforces.com/contest/820/problem/b# tag:# difficulty:from typing import List INF = float("inf")NINF = float("-inf")  def read_string():    return input()  def read_string_line():    return [x for x in input().split(" ")]  def read_int_line():    return [int(x) for x in input().split(" ")]  def read_int():    return int(input())  def get_int_arr(s):    return [int(x) for x in s.split()]  def get_sum(acc, l, r):    """     l, r are original index     """    return acc[r] - acc[l]  P = int(1e9 + 7)  def exgcd(x, y):    if y == 0:        return x, 1, 0    g, a, b = exgcd(y, x % y)    t = x // y    return g, b, a - t * b  def inv(x):    g, a, b = exgcd(x, P)    return a  def calc_acc(arr):    ans = [0]    for a in arr:        ans.append(a + ans[-1])    return ans  lines: List[str] = [*open(0)]n, a = get_int_arr(lines[0])lo = 1hi = n - 1best = INFans = Nonewhile lo < hi:    m = lo + (hi - lo) // 2    cur = abs(m / n * 180 - a)    if cur < best:        best = cur        ans = m    if m / n * 180 > a:        hi = m    else:        lo = m + 1print("%d %d %d" % (1, ans + 2, ans + 1))
