import java.util.*;public class Solution {	public static void main(String[] args) {		Scanner in=new Scanner(System.in);		int n=in.nextInt();		int[][] a=new int[n][n];		for (int i=0;i<n;i++) {for (int j=0;j<n;j++) {a[i][j]=in.nextInt();}}		for (int[] ia:a) {			o:			for (int i=0;i<n;i++) {				if (ia[i]!=1) {					for (int j=0;j<n;j++) {						for (int k=0;k<n;k++) {							if (a[k][i]+ia[j]==ia[i]) {								continue o;							}						}					}					System.out.println("No");					in.close();					return;				}			}		}		System.out.println("Yes");		in.close();	}}
