import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        String first=s.nextLine();        String second=s.nextLine();        String text=s.nextLine();        String result="";int found=0;char c=' ';        for(int i=0;i<text.length();i++){            found=0;            if(!Character.isDigit(text.charAt(i))){                if(Character.isUpperCase(text.charAt(i))){                    found=1;                    c=Character.toLowerCase(text.charAt(i));                }                else                    c=text.charAt(i);                int index=search(first,c);                if(found==1){                    result=result+Character.toUpperCase(second.charAt(index));                }                else                    result=result+second.charAt(index);                            }            else                result=result+text.charAt(i);        }        System.out.println(result);                   }    public static int search(String first,char c){        int index=0;        for(int i=0;i<first.length();i++){            if(first.charAt(i)==c)            {                index=i;                break;            }        }        return index;    }} 
