import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        char a= sc.next().charAt(0);        char b= sc.next().charAt(0);        int n=sc.nextInt();        int x=0, y=0;        if(a=='v')            x=1;        else if(a=='<')            x=2;        else if(a=='^')            x=3;        else if(a=='>')            x=4;                if(b=='v')            y=1;        else if(b=='<')            y=2;        else if(b=='^')            y=3;        else if(b=='>')            y=4;                    int d=((y-x) +4) % 4;        if(d==0 || d==2)        {            System.out.println("undefined");        }        else if(d==n%4)        {            System.out.println("cw");        }        else         {            System.out.println("ccw");        }    }  } 
