//package Coding; import java.io.*;import java.nio.Buffer;import java.util.*; public class CodeForces {    public static void main(String[] args) throws FileNotFoundException {        Scanner in = new Scanner(System.in);        String[] line = in.nextLine().split("\\s+");        int n = Integer.parseInt(line[0]);        int k = Integer.parseInt(line[1]);        String g = in.nextLine();        Map<Character, Integer> gs = new HashMap<>();        int[] current = new int[26];        int needed = 0;        for (int i = 0; i < n; ++i) {            gs.merge(g.charAt(i), 1, Integer::sum);        }        String ans = "NO";         for (int i = 0; i < n; ++i) {            char c = g.charAt(i);            int ci = c - 'A';            if (current[ci] == 0) {                needed++;            }            current[ci]++;            if (needed > k) {                ans = "YES";                break;            }            if (current[ci] == gs.get(c)) {                needed--;            }        }          System.out.println(ans);     }  }          
