import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        int a[]=new int[n];        for(int i=0;i<n;i++)            a[i]=s.nextInt();        int odd=0,even=0;        for(int j=0;j<n;j++){            if(a[j]%2==0)                even++;            else{                odd++;            }        }        if(odd>=1)            System.out.println("First");        else            System.out.println("Second");            }}
