import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        String s1=s.nextLine();        int k=s.nextInt();        HashMap<Character,Integer> h=new HashMap<>();        for(int i=0;i<s1.length();i++){            if(h.containsKey(s1.charAt(i)))                h.put(s1.charAt(i),h.get(s1.charAt(i))+1);            else                h.put(s1.charAt(i),1);        }                if(k>s1.length())            System.out.println("impossible");        else if(h.size()>=k)            System.out.println("0");        else        {                        int count=k-h.size();            System.out.println(count);        }                             }}
