//I AM THE CREED/* //I AM THE CREED/* package codechef; // don't place package name! */import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.StringTokenizer; import java.util.*;import java.awt.Point;public class Main{    //a.push_back        public static void main(String[] args) throws IOException     {           Scanner input = new Scanner(System.in);        while(input.hasNext()){            int n=input.nextInt();            int[] zeroes=new int[n];            int[] ones=new int[n];            int[] l=new int[n];            for(int i=0;i<n;i++){                l[i]=input.nextInt();            }            for(int i=0;i<n;i++){                if(i==0){                    ones[i]=(l[i]==1?1:0);                    continue;                }                ones[i]=ones[i-1]+(l[i]==1?1:0);            }            for(int i=n-1;i>=0;i--){                if(i==n-1){                    zeroes[i]=(l[i]==1?0:1);                    continue;                }                zeroes[i]=zeroes[i+1]+(l[i]==1?0:1);            }            int max=n-Math.min(zeroes[0], ones[n-1]);            for(int i=0;i<n-1;i++){                max=Math.max(max, n-(ones[i]+(zeroes[i+1])));            }            System.out.println(max);        }    }Â }
