import java.io.*;import java.util.*;public class Solution{	public static void main(String[] args) throws Exception{		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));		StringTokenizer stt;		int n=Integer.parseInt(br.readLine());		int records[]=new int[n], succ_records[]=new int[n], unsucc_records[]=new int[n], count=Integer.MIN_VALUE, count_zero=0, count_one=0;		stt=new StringTokenizer(br.readLine());		for(int i=0; i<n; i++){		    records[i]=Integer.parseInt(stt.nextToken());		    count_zero=(records[i]==0)?count_zero+1:count_zero;		    unsucc_records[i]=count_zero;		}		for(int i=n-1; i>=0; i--){		    count_one=(records[i]==1)?count_one+1:count_one;		    succ_records[i]=count_one;		}		count=count_one;		for(int i=0; i<n-1; i++) count=(count<unsucc_records[i]+succ_records[i+1])?unsucc_records[i]+succ_records[i+1]:count;		count=(count<unsucc_records[n-1])?unsucc_records[n-1]:count;		System.out.println(count);		return;	}}
