//Code by Sounak, IIESTimport java.io.*;import java.math.*;import java.util.*;import java.util.Arrays;public class Test1{    public static void main(String args[])throws IOException{        FastReader sc = new FastReader();        StringBuilder sb = new StringBuilder();        int n=sc.nextInt();        int a[]=new int[n];        int i,j,zero=0,to=0,one=0,res=0;        for(i=0;i<n;i++)        {            a[i]=sc.nextInt();            if(a[i]==1)            res++;        }        for(i=0;i<n;i++)        {            if(a[i]==0)            zero++;            one=0;            for(j=i+1;j<n;j++)            {                if(a[j]==1)                one++;            }            res=Math.max(res,zero+one);        }        System.out.println(res);    }}Â class FastReader {    BufferedReader br;    StringTokenizer st;     public FastReader() { br = new BufferedReader(new InputStreamReader(System.in)); }    String next() {        while (st == null || !st.hasMoreElements()) {            try {                st = new StringTokenizer(br.readLine());            } catch (IOException e) {                e.printStackTrace();            }        }        return st.nextToken();    }    int nextInt() { return Integer.parseInt(next()); }    long nextLong() { return Long.parseLong(next()); }    double nextDouble() { return Double.parseDouble(next()); }    String nextLine() {        String str = "";        try {            str = br.readLine();        } catch (IOException e) {            e.printStackTrace();        }        return str;    }}
