import java.io.FileNotFoundException;import java.io.BufferedInputStream;import java.io.FileInputStream;import java.io.PrintStream;import java.util.*;//import java.math.*; /** * * @author TRUE */ public class Main {    public static void main(String args[]) {        Scanner cin;        PrintStream cout;//        try{//            cin = new Scanner(new FileInputStream("in.in"));//            cout = new PrintStream("out.out");//        }catch (FileNotFoundException e) {            cin = new Scanner (new BufferedInputStream(System.in));            cout = System.out;//        }        String ch = "abcdefghijklmnopqrstuvwxyz";        int k;        k = cin.nextInt();        for(int i = 10000, j = 0; i>0; i--) {            while(i*(i-1)/2 <= k) {                k -= i*(i-1)/2;                for(int l = 0; l < i; l++) {                    cout.print(ch.charAt(j));                }                j = j+1;                if(k == 0) {                    break;                }            }        }        cout.print('\n');    }}                  
