import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            int n=input.nextInt();            int a[][]=new int[n][5];            for(int i=0;i<n;i++)            {                for(int j=0;j<5;j++)                {                    a[i][j]=input.nextInt();                }            }            int arr[]=new int[n];            for(int i=0;i<n;i++)            {                if(arr[i]==0)                {                    int flag=0;                    for(int j=0;j<n-1;j++)                    {                        for(int k=j+1;k<n;k++)                        {                            if(j!=i && k!=i)                            {                                int sum=0;                                for(int l=0;l<5;l++)                                {                                    sum+=(a[j][l]-a[i][l])*(a[k][l]-a[i][l]);                                }                                if(sum<=0)                                {                                    arr[j]=-1;                                    arr[k]=-1;                                }                                else                                {                                    flag=1;                                    int s1=0;                                    for(int l=0;l<5;l++)                                    {                                        s1+=(a[i][l]-a[j][l])*(a[k][l]-a[j][l]);                                    }                                    if(s1<=0)                                    {                                        arr[i]=-1;                                        arr[k]=-1;                                    }                                    else                                    {                                        arr[j]=-1;                                    }                                    int s2=0;                                    for(int l=0;l<5;l++)                                    {                                        s2+=(a[i][l]-a[k][l])*(a[j][l]-a[k][l]);                                    }                                    if(s2<=0)                                    {                                        arr[i]=-1;                                        arr[j]=-1;                                    }                                    else                                    {                                        arr[k]=-1;                                    }                                }                            }                        }                    }                    if(flag==1)                    {                        arr[i]=-1;                    }                    else                    {                        arr[i]=1;                    }                }            }            int count=0;            for(int i=0;i<n;i++)            {                if(arr[i]==1)                {                    count++;                }            }            out.println(count);            for(int i=0;i<n;i++)            {                if(arr[i]>=0)                {                    out.println((i+1));                }            }        }        out.close();    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str = "";            try            {                str = br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
