#include <bits/stdc++.h> using namespace std;using pii=pair<int,int>; const int NN = 1010;int vst[NN];vector<int> ans; struct Point {	int a, b, c, d, e;	Point(){}	Point(int a, int b, int c, int d, int e): a(a), b(b), c(c), d(d), e(e){}	void eat() {		cin>>a>>b>>c>>d>>e;	}	int product(const Point& other) {		int ans=a*other.a + b*other.b + c*other.c + d*other.d + e*other.e;		return ans;	}}; Point sub(Point A, Point B) {	return Point(B.a-A.a, B.b-A.b, B.c-A.c, B.d-A.d, B.e-A.e);} Point p[NN];	 int main(){#ifndef ONLINE_JUDGE	freopen("in.in","r",stdin);	freopen("out.out","w",stdout);#endif	int n; cin>>n;	for(int i=1; i<=n; i++) p[i].eat();	for(int i=1; i<=n; i++) {		int flag=1;		if(vst[i]) continue;		for(int j=1; j<=n; j++) {			if(!flag) break;			if(i==j) continue;			for(int k=j+1; k<=n; k++) {				if(!flag) break;				Point a=sub(p[i], p[j]);				Point b=sub(p[i], p[k]);				if(a.product(b)>0) flag=0;				else vst[j]=vst[k]=1;			}		}		if(flag) {			ans.push_back(i);		}	}	cout<<ans.size()<<endl;	for(int u: ans) printf("%d\n", u); 	return 0;}       
