import java.util.*;import java.io.*;public class A{      public static void main(String ar[]) throws Exception      {            BufferedReader br=new BufferedReader(new InputStreamReader(System.in));            int t=Integer.parseInt(br.readLine());            StringBuffer sb=new StringBuffer();            for(int r=0;r<t;r++)            {                  int n=Integer.parseInt(br.readLine());                  String s1[]=br.readLine().split(" ");                  int a[]=new int[n];                  for(int i=0;i<n;i++)                   a[i]=Integer.parseInt(s1[i]);                  Arrays.sort(a);                  boolean b[]=new boolean[1000001];                  Arrays.fill(b,true);                  int k=1;                  sb.append("YES").append("\n");                  int count=0;                  while(count<n)                  {                        while(!b[k])                         k++;                        count++;                        sb.append(k).append(" ");                        for(int i=0;i<n;i++)                        {                              for(int j=i+1;j<n;j++)                              {                                  int u=k+a[j]-a[i];                                  if(u<=1000000 && u>=1)                                   b[u]=false;                              }                        }                        k++;                  }                  sb.append("\n");            }                        System.out.println(sb);      }}
