import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        s.nextLine();        String s1=s.nextLine();        String[] a=new String[400];        HashMap<Character,Integer> h1=new HashMap<>();        String result="";int j=0,max=0;        for(int i=0;i<s1.length();i++){            if(Character.isLowerCase(s1.charAt(i))){                result=result+s1.charAt(i);                        }            else            {                j++;                result="";            }            a[j]=result;        }                for(String h:a)        {                        h1.clear();            if(h!=null)            {                for(int k=0;k<h.length();k++){                    if(h1.containsKey(h.charAt(k)))                        h1.put(h.charAt(k),h1.get(h.charAt(k))+1);                    else                        h1.put(h.charAt(k),1);                }            }            if(h1.size()>max)                max=h1.size();        }            System.out.println(max);    }}
