//       http://codeforces.com/problemset/problem/873/A//       A. Choresimport java.util.Arrays;import java.util.Scanner; public class A12 { 	public static void main(String[] args) { 		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int k = sc.nextInt();		int x = sc.nextInt();		int[] array = new int[n];		for (int i = 0; i < n; i++) {			array[i] = sc.nextInt();		}				Arrays.sort(array);		long sum = 0;				for (int i = 0; i < n-k; i++) {						sum+=array[i];		}		System.out.println(sum+(k*x));	}}
