import java.util.*;import java.util.Arrays;public class Main{    public static void main (String[] args)      {          Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int a=sc.nextInt();        int b=sc.nextInt();        int c=sc.nextInt();        int d=0;        if(c<a && c<b)        {            if(n==1)            {                System.out.println("0");            }            else            {                d=Math.min((a+(c*(n-2))),(b+(c*(n-2))));                System.out.println(d);            }        }        else        {            d=Math.min((a*(n-1)),(b*(n-1)));            System.out.println(d);        }    }  }         
