import java.util.*;public class contest11 {	static Scanner scn = new Scanner(System.in);	public static void main(String[] args) {		int n = scn.nextInt();		int k = scn.nextInt();		int m = scn.nextInt();		int[] arr = new int[n];		for( int i=0; i<n;i++)			arr[i] = scn.nextInt();				HashMap<Integer, List<Integer>> map = new HashMap<>();				for( int i=0;i<n;i++) {			int key = arr[i]%m;			if(!map.containsKey(key))				map.put(key, new ArrayList<>());			map.get(key).add(arr[i]);		}				for( int key:map.keySet()) {			if(map.get(key).size()>=k) {				System.out.println("Yes");				List<Integer> list = map.get(key);				for(int i=0;i<k;i++)					System.out.print(list.get(i)+" ");				return;			}		}				System.out.println("No");	}} 	  		 							 	   	 		      		
